//     <p>Email: {strTeam} </p>
//     <p>Spots type: Football</p>
//     <p><small>ID: {idTeam}  <Link to={`/team/${idTeam}`}>click me</Link></small></p>   
//     <button onClick={()=>handleClick(idTeam)}>show detail</button>
//  </div>
 <Card style={{ width: '18rem', display:'inline-block' }}>
  <Card.Img variant="top" src={strTeamBadge} />
  <Card.Body>
    <Card.Title>{strTeam}</Card.Title>
    <Card.Text>
      <p>Spots type: Football</p>
    </Card.Text>
    {/* <p><small>ID: {idTeam}  <Link to={`/team/${idTeam}`}>click me</Link></small></p>  */}
    <Button variant="primary" onClick={()=>handleClick(idTeam)}>Explore</Button>
  </Card.Body>
</Card> 
    );
};

export default Team;
