      <Card.Body>
        <Card.Title style={{ color: '#6c8e6c', fontSize: '27px', fontWeight: '800' }}>{strTeam}</Card.Title>
        <Card.Text style={{ color: '#A0A0A0' }}>
          Spots type: {strSport}
