        <Link to={`/team/${teamId}`}>
        {/* <p><small>ID: {idTeam}  <Link to={`/team/${idTeam}`}>click me</Link></small></p>  */}
        <Button variant="primary" >Explore   <FontAwesomeIcon icon={faHandPointRight} /></Button>
        </Link>
      </Card.Body>
    </Card>

  );
};

export default Team;
