import { Card , Button} from 'react-bootstrap';
import {  Link, useHistory } from 'react-router-dom';

const Team = (props) => {
const {strTeamBadge, strTeam, idTeam} = props.team
const history =useHistory();
const handleClick = (teamId) =>{
    const url = `/team/${teamId}`;
    history.push(url);
}

return (
