<div style={{backgroundColor:'#0E0A2A', display:'inline-block'}}>
 <Card style={{ width: '18%',maxHeight:'50%',marginLeft:'20px', marginTop:'50px' ,textAlign: 'center', marginBottom:'20px', display:'inline-block' }}>
  <Card.Img variant="top" style={{maxWidth:'60%', marginTop:'15px'}} src={strTeamBadge} />
  <Card.Body>
    <Card.Title style={{fontSize:'25px', fontWidth:'700'}}>{strTeam}</Card.Title>
    <Card.Text style={{color:'#A0A0A0'}}>
      <p>Spots type: Football</p>
    </Card.Text>
    {/* <p><small>ID: {idTeam}  <Link to={`/team/${idTeam}`}>click me</Link></small></p>  */}
    <Button variant="primary" onClick={()=>handleClick(idTeam)}>Explore</Button>
  </Card.Body>
</Card> 
</div>
    );
};

export default Team;
