import React, { useEffect, useState } from 'react';
import { Card } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPodcast, faFlag, faFutbol, faTransgender} from '@fortawesome/free-solid-svg-icons'
const TeamDetail = () => {
    const { teamId } = useParams();
    const [details, setDetails] = useState([]);
    useEffect(() => {
        const url = `https://www.thesportsdb.com/api/v1/json/1/lookupteam.php?id=${teamId}`
        fetch(url)
            .then(res => res.json())
            .then(data => setDetails(data.teams[0]))
    }, [teamId])

    // const teamStyle = {
    //     border: '1px solid purple',
    //     margin: '20px',
    //     padding: '20px',
    //     borderRadius: '20px'
       
    // }
    return (
    
            <Card style={{ backgroundColor:'#3A42FF', borderRadius:'6px', color:'white',maxWidth:'80%', marginLeft:'150px'}}>
                <Card.Header><h2 style={{fontWeight:'800', fontSize:'30px'}}>{details.strTeam} Team</h2></Card.Header>

                <Card.Body style={{display:'flex'}}>
                    <Card.Text style={{color:'white'}}>
                        <p><FontAwesomeIcon icon={faPodcast}/> Founded: {details.intFormedYear}</p>
                        <p><FontAwesomeIcon icon={faFlag}/> Country: {details.strCountry}</p>
                        <p><FontAwesomeIcon icon={faFutbol}/> Spots type: Football</p>
                        <p><FontAwesomeIcon icon={faTransgender}/> Gender: {details.strGender}</p>
                    </Card.Text>
                    <Card.Img style={{maxWidth:'36%', marginLeft:'475px',marginTop:'-64px'}} variant="top" src={details.strTeamFanart1} />
                </Card.Body>

            </Card>

      
    );
};

export default TeamDetail;
